using UnityEngine;public class ButtonBuy : MonoBehaviour{    [SerializeField] private StoreKitGUIManager storeKitGUIManager = null;    [SerializeField] private int id = 0;  protected virtual void OnPress(bool isPressed)  {    if (isPressed)    {      if (id == 2)        storeKitGUIManager.Restore();      else        storeKitGUIManager.Buy(id);    }  }}