using UnityEngine;public class InterfaceScale : MonoBehaviour{    [SerializeField] private bool position = true;    [SerializeField] private bool scale = false;    private void Start()  {      if (position)          transform.localPosition = new Vector3(transform.localPosition.x, transform.localPosition.y * 0.5625f * Screen.height / Screen.width, transform.localPosition.z);      if (scale)        transform.localScale = new Vector3(1,0.5625f * Screen.height/Screen.width ,1);  }}